import React, { useState } from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import { CgNotes } from 'react-icons/cg';
import Divider from '@material-ui/core/Divider';
import { DragPreviewImage, useDrag } from 'react-dnd';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      margin: 'auto',
      color: 'rgb(51,51,51)',
      fontSize: '16px',
      width: '100%',
    },
    title: {
      padding: '10px',
      fontSize: '22px',
    },
    box: {
      position: 'sticky',
      height: '100px',
      cursor: 'pointer',
      display: 'flex',
      flexDirection: 'column',
    },
    list: {
      '&:active': {
        opacity: '0.3',
      },
    },
    noteTitle: {
      padding: '10px 20px 0px 20px',
      color: 'rgb(61,61,61)',
    },
    noteContent: {
      padding: '5px 20px',
      fontSize: '14px',
      color: 'rgb(115,115,115)',
    },
    empty: {
      alignItems: 'center',
      justifyContent: 'center',
      margin: 'auto',
      textAlign: 'center',
      padding: '35px',
    },
    emptyIcon: {
      fontSize: '75px',
    },
    newNoteText: {
      cursor: 'pointer',
      color: 'rgb(0,168,45)',
    },
  })
);

export default function NoteList(props: any) {
  const classes = useStyles();
  const [{ opacity }, drag, preview] = useDrag({
    item: { type: 'note' },
    collect: (monitor) => ({
      opacity: monitor.isDragging() ? 0.4 : 1,
    }),
  });

  return (
    <div className={classes.root}>
      <div className={classes.title}>title</div>
      <Divider />
      {props.notebooks[props.notebook].notes.length > 0 ? (
        <>
          {props.notebooks[props.notebook].notes.map((note: any) => {
            return (
              <div key={note.id}>
                <DragPreviewImage
                  connect={preview}
                  src="../static/images/o-logo.png"
                />
                <NoteListItem noteTitle={noteTitle} note={note} />
                <div className={classes.box}>
                  <div ref={drag} style={{ opacity }} className={classes.list}>
                    <div className={classes.noteTitle}>{note.noteTitle}</div>
                    <div className={classes.noteContent}>{note.note}</div>
                  </div>
                </div>
              </div>
            );
          })}
        </>
      ) : (
        <div className={classes.empty}>
          <div className={classes.emptyIcon}>
            <CgNotes />
          </div>
          <div>It all begins with notes</div>
          <div>
            Click the{' '}
            <span
              onClick={() => props.addNewNote()}
              className={classes.newNoteText}
            >
              + New Note
            </span>{' '}
            button in the side bar to create note.
          </div>
        </div>
      )}
    </div>
  );
}
