import React from 'react';
import { DataGrid, ColDef, ValueGetterParams } from '@material-ui/data-grid';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import INotebook from './interfaces/inotebook';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      alignItems: 'center',
      justifyItems: 'center',
      flexDdirection: 'row',
      height: '100vh',
      width: '100%',
      margin: 'auto',
      color: 'rgb(51,51,51)',
      fontSize: '16px',
    },
  })
);

interface INotebooksProps {
  notebooks: INotebook;
  notebookOrder: Array<string>;
}

export default function Notebooks(props: INotebooksProps) {
  const classes = useStyles();

  const getTimeMsg = (updatedAt: Date) => {
    const createdTime = new Date(updatedAt).valueOf();
    const currentTime = new Date().valueOf();
    const second = 60;
    const minute = 60;
    const hour = 24;
    const day = 30;
    const month = 12;
    let divider = 1;
    let timeMsg = '';
    let diff = (currentTime - createdTime) / 1000;
    if (diff <= second) {
      timeMsg = 'second';
    } else if (diff <= second * minute) {
      divider = second;
      timeMsg = 'minute';
    } else if (diff <= second * minute * hour) {
      divider = second * minute;
      timeMsg = 'hour';
    } else if (diff <= second * minute * hour * day) {
      divider = second * minute * hour;
      timeMsg = 'day';
    } else if (diff <= second * minute * hour * day * month) {
      divider = second * minute * hour * day;
      timeMsg = 'month';
    } else {
      divider = second * minute * hour * day * month;
      timeMsg = 'year';
    }
    diff /= divider;
    const displayTime = Math.abs(Math.round(diff));
    const plural = displayTime > 1 ? 's' : '';
    const timeMessage = `${displayTime} ${timeMsg}${plural} ago`;
    return timeMessage;
  };

  const columns: ColDef[] = [
    { field: 'id', headerName: 'ID', width: 70 },
    { field: 'title', headerName: 'Title', width: 250 },
    { field: 'createdAt', headerName: 'Created Date', width: 300 },
    { field: 'updatedAt', headerName: 'Updated Date', width: 300 },
  ];

  // const rows: Array = [];
  const rows = props.notebookOrder.map((notebook: string, index: number) => {
    return {
      id: index,
      title: props.notebooks[notebook].title,
      createdAt: getTimeMsg(props.notebooks[notebook].createdAt),
      updatedAt: getTimeMsg(props.notebooks[notebook].updatedAt),
    };
    // row.push(row);
  });
  // [
  //   { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
  //   { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
  //   { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
  //   { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
  //   { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
  //   { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
  //   { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
  //   { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
  //   { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
  // ];

  return (
    <div className={classes.root}>
      <DataGrid rows={rows} columns={columns} pageSize={10} />
    </div>
  );
}
