import React, { useState } from 'react';
// import Disclaimer from './disclaimer';
import Sidebar from './sidebar';
import Note from './note';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import { v4 as uuidv4 } from 'uuid';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
    },
  })
);

export default function Main() {
  const classes = useStyles();
  const [open, setOpen] = useState(true);
  const [state, setState] = useState({
    columns: {
      'column-0': {
        id: 'column-0',
        title: 'Plan',
        cards: [
          {
            id: uuidv4(),
            cardTitle: 'Create HTML skeleton',
            note: '',
            isCardCompleted: false,
            isArchived: false,
            checklists: [],
            activities: [],
            dueDate: new Date(2020, 9, 11),
            createdAt: new Date(2020, 8, 11),
            updatedAt: new Date(2020, 8, 11),
          },
        ],
        colorIndex: 0,
        createdAt: new Date(2020, 8, 1),
        updatedAt: new Date(2020, 8, 11),
      },
      'column-1': {
        id: 'column-1',
        title: 'Progress',
        cards: [],
        colorIndex: 1,
        createdAt: new Date(2020, 8, 2),
        updatedAt: new Date(2020, 8, 2),
      },
      'column-2': {
        id: 'column-2',
        title: 'Complete',
        cards: [],
        colorIndex: 2,
        createdAt: new Date(2020, 8, 3),
        updatedAt: new Date(2020, 8, 3),
      },
    },
    columnOrder: ['column-0', 'column-1', 'column-2'],
    archive: {
      id: 'archive',
      title: 'Archive',
      cards: [],
      colorIndex: 0,
      createdAt: new Date(),
      updatedAt: new Date(),
    },
  } as any);
  const notebooks = [{ title: 'Work', createdAt: new Date() }];

  const addNewNote = () => {
    console.log('add new note');
  };
  return (
    <>
      <div className={classes.root}>
        <CssBaseline />
        <Sidebar setOpen={setOpen} />
        <Note addNewNote={addNewNote} open={open} />
      </div>
    </>
  );
}
