import React, { useState } from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import { Box } from '@chakra-ui/core';
const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexDdirection: 'row',
      height: '100vh',
    },
    box: {
      display: 'flex',
      flexDdirection: 'column',
      background: '#ccc',
      padding: '10px',
      boxSizing: 'border-box',
      position: 'relative',
      overflowY: 'hidden',
      height: '100vh',
      width: '200px',
      minWidth: '150px',
      maxWidth: '300px',
      resize: 'horizontal',
    },
    handle: {
      width: '3px',
      backgroundColor: 'black',
      cursor: 'ew-resize',
    },
  })
);

export default function NoteList() {
  const classes = useStyles();
  const listBoxRef = React.createRef();

  return (
    <Box className={classes.root}>
      <Box className={classes.box} ref={listBoxRef}>
        <span>Contents</span>
      </Box>
      <Box
        draggable={true}
        className={classes.handle}
        onDragStart={(e) => console.log(listBoxRef.current.offsetWidth)}
        onDrag={(e) => console.log(listBoxRef)}
      ></Box>
    </Box>
  );
}
