import React, { useState } from 'react';
import clsx from 'clsx';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import IconButton from '@material-ui/core/IconButton';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import DeleteOutlineRoundedIcon from '@material-ui/icons/DeleteOutlineRounded';
import NoteRoundedIcon from '@material-ui/icons/NoteRounded';
import Tooltip from '@material-ui/core/Tooltip';
import AddIcon from '@material-ui/icons/Add';
import ImportContactsIcon from '@material-ui/icons/ImportContacts';
import IMainProps from './interfaces/imainprops';

const drawerWidth = 240;

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    paper: {
      padding: '5px',
    },
    appBar: {
      zIndex: theme.zIndex.drawer + 1,
      transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
    },
    appBarShift: {
      marginLeft: drawerWidth,
      width: `calc(100% - ${drawerWidth}px)`,
      transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
      }),
    },
    menuButton: {
      marginRight: 36,
    },
    hide: {
      display: 'none',
    },
    drawer: {
      width: drawerWidth,
      flexShrink: 0,
      whiteSpace: 'nowrap',
    },
    drawerOpen: {
      width: drawerWidth,
      transition: theme.transitions.create('width', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
      }),
      backgroundColor: 'rgb(26,26,26)',
      color: 'rgb(165,165,165)',
      padding: '5px',
    },
    drawerClose: {
      transition: theme.transitions.create('width', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      overflowX: 'hidden',
      width: theme.spacing(7) + 1,
      [theme.breakpoints.up('sm')]: {
        width: theme.spacing(7) + 1,
      },
      backgroundColor: 'rgb(26,26,26)',
      padding: '5px 0px',
    },
    toolbar: {
      position: 'absolute',
      bottom: '0',
      right: '0',
      padding: theme.spacing(0, 1),
      ...theme.mixins.toolbar,
    },
    folder: {
      padding: '0px 10px',
      '&:hover': {
        backgroundColor: 'rgb(51,51,51)',
        borderRadius: '3px',
      },
    },
    listSelected: {
      backgroundColor: 'rgb(64,64,64)',
      borderRadius: '3px',
      '&:hover': {
        backgroundColor: 'rgb(51,51,51)',
        borderRadius: '3px',
      },
    },
    list: {
      '&:hover': {
        backgroundColor: 'rgb(51,51,51)',
        borderRadius: '3px',
      },
    },
    icon: {
      color: 'rgb(165,165,165)',
    },
    addNoteIcon: {
      color: 'white',
    },
    addNotebookIcon: {
      color: 'rgb(165,165,165)',
      marginLeft: '10px',
    },
    addNote: {
      backgroundColor: 'rgb(0,168,45)',
      color: 'white',
      borderRadius: '50px',
      '&:hover': {
        backgroundColor: 'rgb(0,143,38)',
      },
      padding: '8px',
      margin: 'auto',
      height: '40px',
    },
    newnotebook: {
      width: '100%',
      padding: '10px',
      outline: 'none',
      backgroundColor: 'rgb(165, 165, 165, 0.7)',
      borderRadius: '5px',
    },
  })
);

interface SidebarProps extends IMainProps {
  setNotebook: (notebook: string) => void;
  handleNotebookClick: (notebookId: string) => void;
}

export default function Sidebar(props: SidebarProps) {
  const classes = useStyles();
  const [open, setOpen] = useState<boolean>(true);
  const [showButton, setShowButton] = useState<boolean>(false);
  const [addNotebook, setAddNotebook] = useState<boolean>(false);
  const [newNotebook, setNewNotebook] = useState<string>('');
  const handleDrawerToggle = () => {
    setOpen(!open);
  };

  const addNewNotebook = () => {
    setAddNotebook(true);
  };

  return (
    <Drawer
      variant="permanent"
      className={clsx(classes.drawer, {
        [classes.drawerOpen]: open,
        [classes.drawerClose]: !open,
      })}
      classes={{
        paper: clsx({
          [classes.drawerOpen]: open,
          [classes.drawerClose]: !open,
        }),
      }}
    >
      <List>
        <ListItem key="o-note">
          <ListItemIcon>
            <img
              src="../../static/images/o-logo.png"
              style={{ width: '22px' }}
            />
          </ListItemIcon>
          <ListItemText primary="o-note" />
        </ListItem>
      </List>
      <List>
        <ListItem
          button
          key="add-note"
          className={classes.addNote}
          style={{ width: open ? '85%' : '70%' }}
        >
          <ListItemIcon>
            <Tooltip title="Click to add a note" arrow>
              <AddIcon className={classes.addNoteIcon} />
            </Tooltip>
          </ListItemIcon>
          <Tooltip title="Click to add a note" arrow>
            <ListItemText
              primary="New Note"
              onClick={() => props.addNewNote('', '')}
            />
          </Tooltip>
        </ListItem>
      </List>
      <List>
        <ListItem
          button
          key="Notebooks"
          className={classes.folder}
          style={{ padding: open ? '0px 10px' : '0px 15px' }}
          onMouseLeave={() => setShowButton(false)}
          onMouseOver={() => setShowButton(true)}
        >
          <ListItemIcon>
            <NoteRoundedIcon className={classes.icon} />
          </ListItemIcon>
          <ListItemText primary="Notebooks" />
          {showButton && open && (
            <ListItemIcon
              onClick={() => {
                addNewNotebook();
              }}
            >
              <AddIcon className={classes.addNotebookIcon} />
            </ListItemIcon>
          )}
        </ListItem>
        {props.notebookOrder.length > 0
          ? props.notebookOrder.map((notebook: any) => {
              return (
                <ListItem
                  button
                  key={props.notebooks[notebook].id}
                  className={
                    props.notebook === notebook
                      ? classes.listSelected
                      : classes.list
                  }
                  onClick={() => props.handleNotebookClick(notebook)}
                  style={{
                    padding: open ? '0px 0px 0px 30px' : '0px 15px',
                  }}
                >
                  <ListItemIcon>
                    <ImportContactsIcon className={classes.icon} />
                  </ListItemIcon>
                  <ListItemText primary={props.notebooks[notebook].title} />
                </ListItem>
              );
            })
          : ''}
        {addNotebook && (
          <ListItem
            // onClick={() => props.handleNotebookClick(notebook)}
            style={{
              padding: open ? '0px 0px 0px 30px' : '0px 15px',
            }}
          >
            <ListItemIcon>
              <ImportContactsIcon className={classes.icon} />
            </ListItemIcon>
            <input
              autoFocus
              value={newNotebook}
              placeholder="New Notebook"
              className={classes.newnotebook}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                setNewNotebook(e.target.value)
              }
              onBlur={() => setAddNotebook(false)}
            />
          </ListItem>
        )}
      </List>
      <List>
        <ListItem
          button
          key="Trash"
          className={classes.folder}
          onClick={() => props.handleNotebookClick('trash')}
          style={{ padding: open ? '0px 10px' : '0px 15px' }}
        >
          <ListItemIcon>
            <DeleteOutlineRoundedIcon className={classes.icon} />
          </ListItemIcon>
          <ListItemText primary="Trash" />
        </ListItem>
      </List>
      <div className={classes.toolbar}>
        <IconButton onClick={handleDrawerToggle} className={classes.icon}>
          {open ? (
            <Tooltip title="Click to close" arrow>
              <ChevronLeftIcon />
            </Tooltip>
          ) : (
            <Tooltip title="Click to open" arrow>
              <ChevronRightIcon />
            </Tooltip>
          )}
        </IconButton>
      </div>
    </Drawer>
  );
}
