import React from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import INotebook from './interfaces/inotebook';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      // alignItems: 'center',
      // justifyItems: 'center',
      // flexDdirection: 'row',
      height: '100vh',
      width: '90%',
      margin: 'auto',
    },
    box: {
      margin: '40px',
      color: 'rgb(51,51,51)',
      fontSize: '14px',
    },
    table: {
      minWidth: 700,
    },
  })
);

interface INotebooksProps {
  notebooks: INotebook;
  notebookOrder: Array<string>;
}

export default function Notebooks(props: INotebooksProps) {
  const classes = useStyles();

  const getTimeMsg = (date: Date) => {
    const createdTime = new Date(date).valueOf();
    const currentTime = new Date().valueOf();
    const second = 60;
    const minute = 60;
    const hour = 24;
    const day = 30;
    const month = 12;
    let divider = 1;
    let timeMsg = '';
    let diff = (currentTime - createdTime) / 1000;
    if (diff <= second) {
      timeMsg = 'second';
    } else if (diff <= second * minute) {
      divider = second;
      timeMsg = 'minute';
    } else if (diff <= second * minute * hour) {
      divider = second * minute;
      timeMsg = 'hour';
    } else if (diff <= 172800) {
      return 'Yesterday';
    } else {
      return `${date.getMonth()} ${date.getDate()}, ${date.getFullYear()}`;
    }
    diff /= divider;
    const displayTime = Math.abs(Math.round(diff));
    const plural = displayTime > 1 ? 's' : '';
    const timeMessage = `${displayTime} ${timeMsg}${plural} ago`;
    return timeMessage;
  };

  const createData = (
    title: string,
    noteCount: number,
    createdAt: Date,
    updatedAt: Date
  ) => {
    return { title, noteCount, createdAt, updatedAt };
  };

  const rows = props.notebookOrder.map((notebook: string, index: number) => {
    return createData(
      props.notebooks[notebook].title,
      props.notebooks[notebook].notes.length,
      props.notebooks[notebook].createdAt,
      props.notebooks[notebook].updatedAt
    );
  });

  return (
    <div className={classes.root}>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="customized table">
          <TableHead>
            <TableRow>
              <TableCell>Notebook</TableCell>
              <TableCell align="right">Number of Notes</TableCell>
              <TableCell align="right">Created Date</TableCell>
              <TableCell align="right">Update Date</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <TableRow key={row.id}>
                <TableCell component="th" scope="row">
                  {row.title}
                </TableCell>
                <TableCell align="right">{row.noteCount}</TableCell>
                <TableCell align="right">{row.createdAt}</TableCell>
                <TableCell align="right">{row.updatedAt}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
