import React, { useState } from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import { CgNotes } from 'react-icons/cg';
import Divider from '@material-ui/core/Divider';
import { usePreview } from 'react-dnd-preview';
import { useDrag, useDrop, DndProvider, DragSourceMonitor } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      margin: 'auto',
      color: 'rgb(51,51,51)',
      fontSize: '16px',
      width: '100%',
    },
    title: {
      padding: '10px',
      fontSize: '22px',
    },
    box: {
      position: 'sticky',
      height: '100px',
      cursor: 'pointer',
      display: 'flex',
      flexDirection: 'column',
    },
    list: {
      '&:active': {
        opacity: '0.3',
      },
    },
    noteTitle: {
      padding: '10px 20px 0px 20px',
      color: 'rgb(61,61,61)',
    },
    noteContent: {
      padding: '5px 20px',
      fontSize: '14px',
      color: 'rgb(115,115,115)',
    },
    empty: {
      alignItems: 'center',
      justifyContent: 'center',
      margin: 'auto',
      textAlign: 'center',
      padding: '35px',
    },
    emptyIcon: {
      fontSize: '75px',
    },
    newNoteText: {
      cursor: 'pointer',
      color: 'rgb(0,168,45)',
    },
  })
);

interface Note {
  type: string;
  id: number;
  originalIndex: number;
}

export default function NoteList(props: any) {
  const classes = useStyles();
  const [{ isDragging }, drag] = useDrag({
    item: { type: 'note' },
    end: (monitor: DragSourceMonitor) => {
      const dropResult = monitor.getDropResult();
    },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });
  const opacity = isDragging ? 0.4 : 1;

  const [{ isOver }, drop] = useDrop({
    accept: 'note',
    collect: (monitor: any) => ({
      isOver: monitor.isOver(),
    }),
    hover({ id: draggedId }: Note) {},
  });

  const MyPreview = () => {
    const { display, itemType, item, style } = usePreview();
    if (!display) {
      return null;
    }
    return <div style={style}>{itemType}</div>;
  };

  return (
    <div className={classes.root}>
      <div className={classes.title}>title</div>
      <Divider />
      {props.notebooks[props.notebook].notes.length > 0 ? (
        <>
          {props.notebooks[props.notebook].notes.map((note: any) => {
            return (
              <div key={note.id}>
                {/* <div className={classes.box}>
                  <div draggable className={classes.list}>
                    <div className={classes.noteTitle}>{note.noteTitle}</div>
                    <div className={classes.noteContent}>{note.note}</div>
                  </div>
                </div> */}
                <DndProvider backend={HTML5Backend}>
                  <MyPreview />
                  <div className={classes.box}>
                    <div
                      ref={drag}
                      style={{ opacity }}
                      className={classes.list}
                    >
                      <div className={classes.noteTitle}>{note.noteTitle}</div>
                      <div className={classes.noteContent}>{note.note}</div>
                    </div>
                  </div>
                </DndProvider>
              </div>
            );
          })}
        </>
      ) : (
        <div className={classes.empty}>
          <div className={classes.emptyIcon}>
            <CgNotes />
          </div>
          <div>It all begins with notes</div>
          <div>
            Click the{' '}
            <span
              onClick={() => props.addNewNote()}
              className={classes.newNoteText}
            >
              + New Note
            </span>{' '}
            button in the side bar to create note.
          </div>
        </div>
      )}
    </div>
  );
}
