import React, { useState } from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import { CgNotes } from 'react-icons/cg';
import Divider from '@material-ui/core/Divider';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      margin: 'auto',
      color: 'rgb(51,51,51)',
      fontSize: '16px',
      width: '100%',
    },
    title: {
      padding: '10px',
      fontSize: '22px',
    },
    list: {
      height: '100px',
      cursor: 'pointer',
      '&:active': {
        height: '30px',
        width: '100px',
      },
    },
    listDragging: {
      height: '100px',
      border: '2px groove rgb(0,119,179)',
      borderRadius: '5px',
    },
    content: {
      padding: '20px',
      height: '100px',
    },
    empty: {
      alignItems: 'center',
      justifyContent: 'center',
      margin: 'auto',
      textAlign: 'center',
      padding: '35px',
    },
    emptyIcon: {
      fontSize: '75px',
    },
    newNoteText: {
      cursor: 'pointer',
      color: 'rgb(0,168,45)',
    },
  })
);
export default function NoteList(props: any) {
  const classes = useStyles();

  const handleDragStart = (noteId: string, note: any) => {
    const newNote = {
      ...note,
      dragging: true,
    };
    props.updateNote(props.notebook, noteId, newNote);
  };

  const handleDragEnd = (noteId: string, note: any) => {
    const newNote = {
      ...note,
      dragging: false,
    };
    props.updateNote(props.notebook, noteId, newNote);
  };

  return (
    <div className={classes.root}>
      <div className={classes.title}>title</div>
      <Divider />
      {props.notebooks[props.notebook].notes.length > 0 ? (
        <>
          {props.notebooks[props.notebook].notes.map((note: any) => {
            return (
              <div key={note.id}>
                {note.dragging ? (
                  <div
                    onMouseUp={() => handleDragEnd(note.id, note)}
                    className={classes.listDragging}
                  >
                    <div className={classes.content}>{note.noteTitle}</div>
                  </div>
                ) : (
                  <div
                    onMouseDown={() => handleDragStart(note.id, note)}
                    className={classes.list}
                    draggable
                  >
                    <div className={classes.content}>{note.noteTitle}</div>
                  </div>
                )}
                <Divider />
              </div>
            );
          })}
        </>
      ) : (
        <div className={classes.empty}>
          <div className={classes.emptyIcon}>
            <CgNotes />
          </div>
          <div>It all begins with notes</div>
          <div>
            Click the{' '}
            <span
              onClick={() => props.addNewNote()}
              className={classes.newNoteText}
            >
              + New Note
            </span>{' '}
            button in the side bar to create note.
          </div>
        </div>
      )}
    </div>
  );
}
