import React, { useState } from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import { CgNotes } from 'react-icons/cg';
import Divider from '@material-ui/core/Divider';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      margin: 'auto',
      color: 'rgb(51,51,51)',
      fontSize: '16px',
      width: '100%',
    },
    title: {
      padding: '10px',
      fontSize: '22px',
    },
    list: {
      height: '100px',
      cursor: 'pointer',
      display: 'flex',
      flexDirection: 'column',
      '&:focus': {
        width: '80px',
        height: '30px',
        border: '2px dashed blue',
        borderRadius: '5px',
      },
    },
    noteTitle: {
      padding: '5px 20px',
      // height: '100px',
    },
    noteContent: {
      padding: '5px 20px',
    },
    draggingContent: {
      height: '100px',
      cursor: 'pointer',
      display: 'flex',
      flexDirection: 'column',
      opacity: '0.3',
      borderRadius: '5px',
    },
    empty: {
      alignItems: 'center',
      justifyContent: 'center',
      margin: 'auto',
      textAlign: 'center',
      padding: '35px',
    },
    emptyIcon: {
      fontSize: '75px',
    },
    newNoteText: {
      cursor: 'pointer',
      color: 'rgb(0,168,45)',
    },
  })
);
export default function NoteList(props: any) {
  const [isDragging, setIsDragging] = useState(false);
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <div className={classes.title}>title</div>
      <Divider />
      {props.notebooks[props.notebook].notes.length > 0 ? (
        <>
          {props.notebooks[props.notebook].notes.map((note: any) => {
            return (
              <div key={note.id}>
                {isDragging ? (
                  <div
                    className={classes.draggingContent}
                    onDragEnd={() => {
                      setIsDragging(false);
                    }}
                  >
                    <div className={classes.noteTitle}>{note.noteTitle}</div>
                    <div className={classes.noteContent}>{note.note}</div>
                  </div>
                ) : (
                  <div
                    className={classes.list}
                    draggable
                    onDrag={() => {
                      setIsDragging(true);
                    }}
                  >
                    <div className={classes.noteTitle}>{note.noteTitle}</div>
                    <div className={classes.noteContent}>{note.note}</div>
                  </div>
                )}
                <Divider />
              </div>
            );
          })}
        </>
      ) : (
        <div className={classes.empty}>
          <div className={classes.emptyIcon}>
            <CgNotes />
          </div>
          <div>It all begins with notes</div>
          <div>
            Click the{' '}
            <span
              onClick={() => props.addNewNote()}
              className={classes.newNoteText}
            >
              + New Note
            </span>{' '}
            button in the side bar to create note.
          </div>
        </div>
      )}
    </div>
  );
}
