import React from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import INotebook from './interfaces/inotebook';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      // alignItems: 'center',
      // justifyItems: 'center',
      // flexDdirection: 'row',
      height: '100vh',
      width: '90%',
      margin: 'auto',
    },
    box: {
      margin: '40px',
      color: 'rgb(51,51,51)',
      fontSize: '14px',
    },
    table: {
      minWidth: 700,
    },
  })
);

interface INotebooksProps {
  notebooks: INotebook;
  notebookOrder: Array<string>;
}

export default function Notebooks(props: INotebooksProps) {
  const classes = useStyles();

  const getTimeMsg = (date: Date) => {
    const createdTime = new Date(date).valueOf();
    const currentTime = new Date().valueOf();
    const second = 60;
    const minute = 60;
    const hour = 24;
    const day = 30;
    const month = 12;
    let divider = 1;
    let timeMsg = '';
    let diff = (currentTime - createdTime) / 1000;
    if (diff <= second) {
      timeMsg = 'second';
    } else if (diff <= second * minute) {
      divider = second;
      timeMsg = 'minute';
    } else if (diff <= second * minute * hour) {
      divider = second * minute;
      timeMsg = 'hour';
    } else if (diff <= 172800) {
      return 'Yesterday';
    } else {
      return `${date.getMonth()} ${date.getDate()}, ${date.getFullYear()}`;
    }
    diff /= divider;
    const displayTime = Math.abs(Math.round(diff));
    const plural = displayTime > 1 ? 's' : '';
    const timeMessage = `${displayTime} ${timeMsg}${plural} ago`;
    return timeMessage;
  };

  const columns: ColDef[] = [
    { field: 'id', headerName: 'ID', width: 70 },
    { field: 'title', headerName: 'Notebook', width: 250 },
    { field: 'createdAt', headerName: 'Created Date', width: 300 },
    { field: 'updatedAt', headerName: 'Updated Date', width: 300 },
  ];

  // const rows: Array = [];
  const rows = props.notebookOrder.map((notebook: string, index: number) => {
    return {
      id: index,
      title: props.notebooks[notebook].title,
      createdAt: getTimeMsg(props.notebooks[notebook].createdAt),
      updatedAt: getTimeMsg(props.notebooks[notebook].updatedAt),
    };
    // row.push(row);
  });
  // [
  //   { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
  //   { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
  //   { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
  //   { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
  //   { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
  //   { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
  //   { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
  //   { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
  //   { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
  // ];

  return (
    <div className={classes.root}>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="customized table">
          <TableHead>
            <TableRow>
              <div>Dessert (100g serving)</div>
              <div align="right">Calories</div>
              <div align="right">Fat&nbsp;(g)</div>
              <div align="right">Carbs&nbsp;(g)</div>
              <div align="right">Protein&nbsp;(g)</div>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <StyledTableRow key={row.name}>
                <div component="th" scope="row">
                  {row.name}
                </div>
                <div align="right">{row.calories}</div>
                <div align="right">{row.fat}</div>
                <div align="right">{row.carbs}</div>
                <div align="right">{row.protein}</div>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
