import React, { useEffect, useState } from 'react';
import NoteList from './note-list';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
// import CKEditor from '@ckeditor/ckeditor5-react';
// import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexDdirection: 'row',
      height: '100vh',
    },
    box: {
      display: 'flex',
      flexDdirection: 'column',
      backgroundColor: 'rgb(248, 248, 248)',
      padding: '10px',
      height: '100vh',
      resize: 'horizontal',
      minWidth: '200px',
      width: '300px',
      maxWidth: '400px',
      overflow: 'hidden',
      borderRight: '1px solid rgb(230, 230, 230)',
    },
  })
);

export default function Note(props: any) {
  const [isServer, setIsServer] = useState(true);
  const classes = useStyles();

  useEffect(() => {
    // SSR doesn't fire ComponentDidMount, so we import CKEditor inside of it and store it as a component prop
    const CKEditor = require('@ckeditor/ckeditor5-react');
    const ClassicEditor = require('@ckeditor/ckeditor5-build-classic');
    setIsServer(false); // We just do this to toggle a re-render
  }, []);

  return (
    <>
      <div className={classes.root}>
        <div className={classes.box}>
          <DndProvider backend={HTML5Backend}>
            <NoteList
              notebooks={props.notebooks}
              notebookOrder={props.notebookOrder}
              notebook={props.notebook}
              addNewNote={props.addNewNote}
              updateNote={props.updateNote}
            />
          </DndProvider>
        </div>
        <div>
          {CKEditor && (
            <CKEditor
              editor={ClassicEditor}
              data="<p>Hello from CKEditor 5!</p>"
              onInit={(editor: any) => {
                // You can store the "editor" and use when it is needed.
                console.log('Editor is ready to use!', editor);
              }}
              onChange={(event: any, editor: any) => {
                const data = editor.getData();
                console.log({ event, editor, data });
              }}
              onBlur={(event: any, editor: any) => {
                console.log('Blur.', editor);
              }}
              onFocus={(event: any, editor: any) => {
                console.log('Focus.', editor);
              }}
            />
          )}
        </div>
      </div>
    </>
  );
}
