import React, { useState } from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import { CgNotes } from 'react-icons/cg';
import Divider from '@material-ui/core/Divider';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      margin: 'auto',
      color: 'rgb(51,51,51)',
      fontSize: '16px',
      width: '100%',
    },
    title: {
      padding: '10px',
      fontSize: '22px',
    },
    list: {
      height: '100px',
      cursor: 'pointer',
      display: 'flex',
      flexDirection: 'column',
    },
    noteTitle: {
      padding: '5px 20px',
      // height: '100px',
      '&:active': {
        width: '100%',
        height: '30px',
        border: '1px solid green',
        borderRadius: '5px',
      },
    },
    noteTitleDragging: {
      padding: '5px 20px',
      opacity: '0.3',
    },
    noteContent: {
      padding: '5px 20px',
    },
    noteContentDragging: {
      padding: '5px 20px',
      opacity: '0.3',
    },
    empty: {
      alignItems: 'center',
      justifyContent: 'center',
      margin: 'auto',
      textAlign: 'center',
      padding: '35px',
    },
    emptyIcon: {
      fontSize: '75px',
    },
    newNoteText: {
      cursor: 'pointer',
      color: 'rgb(0,168,45)',
    },
  })
);
export default function NoteList(props: any) {
  const [isDragging, setIsDragging] = useState(false);
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <div className={classes.title}>title</div>
      <Divider />
      {props.notebooks[props.notebook].notes.length > 0 ? (
        <>
          {props.notebooks[props.notebook].notes.map((note: any) => {
            return (
              <div key={note.id}>
                {isDragging ? (
                  <div className={classes.list}>
                    <div
                      onDragEnd={() => {
                        setIsDragging(false);
                      }}
                      className={classes.noteTitleDragging}
                    >
                      {note.noteTitle}
                    </div>
                    <div
                      onDragEnd={() => {
                        setIsDragging(false);
                      }}
                      className={classes.noteContentDragging}
                    >
                      {note.note}
                    </div>
                  </div>
                ) : (
                  <div className={classes.list}>
                    <div
                      draggable
                      onDrag={() => {
                        setIsDragging(true);
                      }}
                      className={classes.noteTitle}
                    >
                      {note.noteTitle}
                    </div>
                    <div
                      draggable
                      onDrag={() => {
                        setIsDragging(true);
                      }}
                      className={classes.noteContent}
                    >
                      {note.note}
                    </div>
                  </div>
                )}
                <Divider />
              </div>
            );
          })}
        </>
      ) : (
        <div className={classes.empty}>
          <div className={classes.emptyIcon}>
            <CgNotes />
          </div>
          <div>It all begins with notes</div>
          <div>
            Click the{' '}
            <span
              onClick={() => props.addNewNote()}
              className={classes.newNoteText}
            >
              + New Note
            </span>{' '}
            button in the side bar to create note.
          </div>
        </div>
      )}
    </div>
  );
}
