import React from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import { DragPreviewImage, useDrag } from 'react-dnd';
import { usePreview } from 'react-dnd-preview';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      flexDirection: 'column',
      height: '100vh',
      margin: 'auto',
      color: 'rgb(51,51,51)',
      fontSize: '16px',
      width: '100%',
    },
    title: {
      padding: '10px',
      fontSize: '22px',
    },
    box: {
      position: 'sticky',
      height: '100px',
      cursor: 'pointer',
      display: 'flex',
      flexDirection: 'column',
    },
    list: {
      '&:active': {
        opacity: '0.3',
      },
    },
    noteTitle: {
      padding: '10px 20px 0px 20px',
      color: 'rgb(61,61,61)',
    },
    noteContent: {
      padding: '5px 20px',
      fontSize: '14px',
      color: 'rgb(115,115,115)',
    },
    empty: {
      alignItems: 'center',
      justifyContent: 'center',
      margin: 'auto',
      textAlign: 'center',
      padding: '35px',
    },
    emptyIcon: {
      fontSize: '75px',
    },
    newNoteText: {
      cursor: 'pointer',
      color: 'rgb(0,168,45)',
    },
  })
);

export default function NoteListItem(props: any) {
  const classes = useStyles();

  const [{ opacity }, drag, preview] = useDrag({
    item: { type: 'note' },
    collect: (monitor) => ({
      opacity: monitor.isDragging() ? 0.4 : 1,
    }),
  });

  const MyPreview = () => {
    const { display, itemType, item, style } = usePreview();
    if (!display) {
      return null;
    }
    return <div style={style}>{itemType}</div>;
  };

  return (
    <>
      <MyPreview />
      <div className={classes.box}>
        <div ref={drag} style={{ opacity }} className={classes.list}>
          <div className={classes.noteTitle}>{props.noteTitle}</div>
          <div className={classes.noteContent}>{props.note}</div>
        </div>
      </div>
    </>
  );
}
