import React, { useState } from 'react';
// import Disclaimer from './disclaimer';
import Sidebar from './sidebar';
import Note from './note';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import { v4 as uuidv4 } from 'uuid';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert, { AlertProps } from '@material-ui/lab/Alert';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
    },
  })
);

function Alert(props: AlertProps) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

export default function Main() {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [message, setMessage] = useState('' as any);
  const [severity, setSeverity] = useState('success' as any);
  const [notebook, setNotebook] = useState('notebook-0');
  const [state, setState] = useState({
    notebooks: {
      'notebook-0': {
        id: 'notebook-0',
        title: 'Work',
        notes: [
          {
            id: uuidv4(),
            noteTitle: 'Tech interview',
            note: '',
            isDeleted: false,
            createdAt: new Date(2020, 8, 10),
            updatedAt: new Date(2020, 8, 10),
          },
          {
            id: uuidv4(),
            noteTitle: 'Meet up',
            note: '',
            isDeleted: false,
            createdAt: new Date(2020, 8, 11),
            updatedAt: new Date(2020, 8, 11),
          },
        ],
        createdAt: new Date(2020, 8, 1),
        updatedAt: new Date(2020, 8, 11),
      },
      'notebook-1': {
        id: 'notebook-1',
        title: 'Personal',
        notes: [
          {
            id: uuidv4(),
            noteTitle: 'Pay Utilities',
            note: '',
            isDeleted: false,
            createdAt: new Date(2020, 8, 11),
            updatedAt: new Date(2020, 8, 11),
          },
        ],
        createdAt: new Date(2020, 8, 1),
        updatedAt: new Date(2020, 8, 11),
      },
    },
    notebookOrder: ['notebook-0', 'notebook-1'],
    trash: {
      id: 'trash',
      title: 'Trash',
      notes: [],
      createdAt: new Date(),
      updatedAt: new Date(),
    },
  } as any);

  const addNewNote = (title: string, note: string) => {
    const newNote = {
      id: uuidv4(),
      noteTitle: 'Untitled',
      note: '',
      isDeleted: false,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    state.notebooks[notebook].notes.unshift(newNote);
    handleSnackbar(`A note is created`, 'info');
  };

  const handleClose = (event?: React.SyntheticEvent, reason?: string) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  const handleSnackbar = (message: string, severity: any) => {
    setMessage(message);
    setSeverity(severity);
    setOpen(true);
  };

  const getItemStyle = (isDragging: any, draggableStyle: any) => ({
    display: 'flex',
    flexDirection: 'row',
    ...draggableStyle,
  });

  const reorder = (list: any, startIndex: any, endIndex: any) => {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);
    return result;
  };

  const onDragEnd = (result: any) => {
    if (!result.destination) {
      return;
    }

    if (result.type === 'column') {
      const columnOrder = reorder(
        state.columnOrder,
        result.source.index,
        result.destination.index
      );
      handleSnackbar(
        `'${
          state.columns[state.columnOrder[result.source.index]].title
        }' and '${
          state.columns[state.columnOrder[result.destination.index]].title
        }' are reordered`,
        'success'
      );
      setState({
        ...state,
        columnOrder,
      } as any);
      return;
    }

    if (result.source.droppableId === result.destination.droppableId) {
      const column = state.columns[result.source.droppableId];
      const cards = reorder(
        column.cards,
        result.source.index,
        result.destination.index
      );

      const newState = {
        ...state,
        columns: {
          ...state.columns,
          [column.id]: {
            ...column,
            cards,
          },
        },
      };
      setState(newState);
      handleSnackbar(`A card is reordered in'${column.title}'`, 'success');
      return;
    }

    const sourceColumn = state.columns[result.source.droppableId];
    const destinationColumn = state.columns[result.destination.droppableId];
    const card = sourceColumn.cards[result.source.index];

    const newSourceColumn = {
      ...sourceColumn,
      cards: [...sourceColumn.cards],
    };
    newSourceColumn.cards.splice(result.source.index, 1);

    const newDestinationColumn = {
      ...destinationColumn,
      cards: [...destinationColumn.cards],
    };
    newDestinationColumn.cards.splice(result.destination.index, 0, card);

    const newState = {
      ...state,
      columns: {
        ...state.columns,
        [newSourceColumn.id]: newSourceColumn,
        [newDestinationColumn.id]: newDestinationColumn,
      },
    };
    handleSnackbar(
      `A card is moved from '${newSourceColumn.title}' to '${newDestinationColumn.title}'`,
      'success'
    );
    setState(newState);
  };

  return (
    <>
      <div className={classes.root}>
        <CssBaseline />
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable
            direction="horizontal"
            droppableId="all-droppables"
            type="notebooks"
          >
            {(provided: any, snapshot: any) => (
              <div
                ref={provided.innerRef}
                {...provided.droppableProps}
                style={getItemStyle(
                  snapshot.isDragging,
                  provided.droppableProps.style
                )}
              >
                <Sidebar
                  notebooks={state.notebooks}
                  notebookOrder={state.notebookOrder}
                  notebook={notebook}
                  addNewNote={addNewNote}
                  setOpen={setOpen}
                  setNotebook={setNotebook}
                />
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
        {/* <Sidebar
          notebooks={state.notebooks}
          notebookOrder={state.notebookOrder}
          notebook={notebook}
          addNewNote={addNewNote}
          setOpen={setOpen}
          setNotebook={setNotebook}
        /> */}
        <Note
          notebooks={state.notebooks}
          notebookOrder={state.notebookOrder}
          notebook={notebook}
          addNewNote={addNewNote}
          open={open}
        />
        <Snackbar
          open={open}
          anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
          autoHideDuration={1000}
          onClose={handleClose}
        >
          <Alert onClose={handleClose} severity={severity}>
            {message}
          </Alert>
        </Snackbar>
      </div>
    </>
  );
}
